
public class Trouble {
	private int number;
	public Trouble(int number) {
		// TODO Auto-generated constructor stub
		this.number = number;
	}
	
	public int getNumber() {
		return number;
	}
	
	@Override
	public String toString() {
		// TODO Auto-generated method stub
		return "[Trouble " + number + "]";
	}

}
